import * as WebSocket from 'universal-websocket-client'
import sleep from './sleep'
import Dispatcher from './dispatcher'

export abstract class TransportBase implements Riptide.Transport {
    abstract write(data: string)
    protected on_data = new Dispatcher<string>()

    subscribe(cb: (data: string) => void) {
        this.on_data.add(cb)
    }
}

export class WS extends TransportBase {
    public attempts = -1
    public on_status = new Dispatcher<string>()
    private socket: WebSocket;

    write(data: string) {
        if (!this.socket || this.socket.readyState !== 1) throw 'Socket unready'
        this.socket.send(data)
    }


    async connect(url: string) {
        this.attempts++
        await sleep(Math.min(this.attempts * 1000, 5 * 1000))
        this.socket = new WebSocket(url)
        this.socket.onopen = () => {
            this.attempts = 0
            this.on_status.trigger('ready')
        }

        this.socket.onclose = () => {
            this.cleanup()
            this.connect(url)
        }

        this.socket.onerror = err => {
            // this._cleanup()
            // this._connect()
        }

        this.socket.onmessage = event => {
            this.on_data.trigger(event.data)
        }
    }

    disconnect() {
        if (!this.socket) return
        this.socket.onclose = () => { }
        this.socket.close()
        this.cleanup()
    }


    private cleanup() {
        this.on_status.trigger('disconnected')
        this.socket = undefined
    }
}
